def singleton(cls):
    """Singleton decorator to ensure only one instance of a class exists."""
    instances = {}

    def get_instance(*args, **kwargs):
        if cls not in instances:
            instances[cls] = cls(*args, **kwargs)
        return instances[cls]

    return get_instance

