func (e *ElevenLabs) GenerateSpeech(text, voiceID string) ([]byte, error) {
	url := fmt.Sprintf("https://api.elevenlabs.io/v1/text-to-speech/%s", "1qEiC6qsybMkmnNdVMbK")

