.voice-container {
  height: calc(100vh - 140px);
  display: flex;
  align-items: center;
  justify-content: center;
}

.voice-card {
  width: 100%;
  max-width: 800px;
  background: var(--surface);
  border-radius: 24px;
  padding: 3rem;
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.3);
  border: 1px solid var(--border);
}

.voice-header {
  text-align: center;
  margin-bottom: 2.5rem;
}

.voice-header h2 {
  font-size: 2rem;
  margin-bottom: 0.5rem;
  background: linear-gradient(135deg, var(--primary), var(--secondary));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.voice-header p {
  color: var(--text-secondary);
  font-size: 1.125rem;
}

.connect-section {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.phone-input-group {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.phone-input-group label {
  font-weight: 500;
  color: var(--text-primary);
  font-size: 1rem;
}

.phone-input {
  padding: 1.25rem;
  background: var(--background);
  border: 2px solid var(--border);
  border-radius: 12px;
  color: var(--text-primary);
  font-size: 1.125rem;
  transition: all 0.2s ease;
}

.phone-input:focus {
  border-color: var(--primary);
  box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1);
}

.phone-input::placeholder {
  color: var(--text-muted);
}

.error-message {
  padding: 1rem;
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid var(--error);
  border-radius: 12px;
  color: var(--error);
  text-align: center;
}

.call-button {
  padding: 1.25rem 2rem;
  border-radius: 16px;
  font-size: 1.125rem;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  transition: all 0.2s ease;
}

.call-button.start {
  background: linear-gradient(135deg, var(--success), #059669);
  color: white;
}

.call-button.start:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 12px 30px rgba(16, 185, 129, 0.4);
}

.call-button.end {
  background: linear-gradient(135deg, var(--error), #dc2626);
  color: white;
}

.call-button.end:hover {
  transform: translateY(-2px);
  box-shadow: 0 12px 30px rgba(239, 68, 68, 0.4);
}

.call-button:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.info-cards {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1rem;
  margin-top: 1rem;
}

.info-card {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1.5rem;
  background: var(--background);
  border: 1px solid var(--border);
  border-radius: 12px;
}

.info-card svg {
  color: var(--primary);
  flex-shrink: 0;
}

.info-card h4 {
  margin: 0 0 0.25rem 0;
  font-size: 0.95rem;
  color: var(--text-primary);
}

.info-card p {
  margin: 0;
  font-size: 0.85rem;
  color: var(--text-muted);
}

.call-section {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

.call-status {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  position: relative;
  padding: 2rem 0;
}

.pulse-ring {
  position: absolute;
  width: 120px;
  height: 120px;
  border: 3px solid var(--success);
  border-radius: 50%;
  animation: pulse-ring 2s cubic-bezier(0.455, 0.03, 0.515, 0.955) infinite;
}

@keyframes pulse-ring {
  0% {
    transform: scale(0.8);
    opacity: 1;
  }
  100% {
    transform: scale(1.4);
    opacity: 0;
  }
}

.call-icon {
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, var(--success), #059669);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  z-index: 1;
}

.call-status h3 {
  margin: 0;
  font-size: 1.5rem;
  color: var(--text-primary);
}

.call-status p {
  margin: 0;
  color: var(--text-secondary);
  font-size: 1rem;
}

.transcript-section {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.transcript-section h4 {
  margin: 0;
  font-size: 1.125rem;
  color: var(--text-primary);
}

.transcript-container {
  max-height: 300px;
  overflow-y: auto;
  background: var(--background);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 1.5rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.transcript-entry {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.speaker-tag {
  font-size: 0.875rem;
  font-weight: 600;
  color: var(--text-secondary);
}

.transcript-entry.agent .speaker-tag {
  color: var(--primary);
}

.transcript-entry.user .speaker-tag {
  color: var(--secondary);
}

.transcript-entry p {
  margin: 0;
  padding: 1rem;
  background: var(--surface);
  border-radius: 8px;
  border-left: 3px solid var(--border);
  color: var(--text-primary);
  line-height: 1.6;
}

.transcript-entry.agent p {
  border-left-color: var(--primary);
}

.transcript-entry.user p {
  border-left-color: var(--secondary);
}

.empty-state {
  text-align: center;
  color: var(--text-muted);
  font-style: italic;
}

.call-controls {
  display: flex;
  gap: 1rem;
  align-items: center;
}

.control-button {
  width: 64px;
  height: 64px;
  border-radius: 16px;
  background: var(--surface-light);
  color: var(--text-primary);
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease;
  border: 1px solid var(--border);
}

.control-button:hover {
  background: var(--border);
}

.control-button.active {
  background: var(--error);
  color: white;
  border-color: var(--error);
}

.call-button.end {
  flex: 1;
}

.spin {
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@media (max-width: 768px) {
  .voice-card {
    padding: 2rem;
  }

  .info-cards {
    grid-template-columns: 1fr;
  }

  .voice-header h2 {
    font-size: 1.5rem;
  }
}

